import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  public credentials = { email: '', password: '' };
  public user: any;

  constructor(public authService: AuthService){}

  public login(){
    this.authService.login(this.credentials).subscribe(
      response => {
        console.log('Login successful', response);
        this.user = response;
      },
      error => {
        console.error('Login failed', error);
      }
    );
  }
}
<h1>Login</h1>
<form (ngSubmit)="login()">
  <mat-form-field appearance="fill">
    <mat-label>Email</mat-label>
    <input matInput type="email" id="email" [(ngModel)]="credentials.email" name="email" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Password</mat-label>
    <input matInput type="password" id="password" [(ngModel)]="credentials.password" name="password" required>
  </mat-form-field>

  <button mat-raised-button color="primary" type="submit">Login</button>
</form>
