import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { NotificationService } from 'src/app/services/notification.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent {
  public user = { username: '', fullName: '', email: '', mobile: '', password: '', address: '' };

  constructor(
    public authService: AuthService,
    public notificationService: NotificationService,
    public router: Router
  ){}

  public signup(){
    this.authService.signup(this.user).subscribe(
      response => {
        this.notificationService.showSuccess('Sign up successful');
        console.log('Sign up successful', response);
        this.router.navigate(['/login']);
      },
      error => {
        this.notificationService.showError('Sign up failed: ' + error.error.message);
        console.error('Sign up failed', error);
      }
    );
  }
}
<h1>Sign Up</h1>
<form (ngSubmit)="signup()" class="login-form">
  <mat-form-field appearance="fill">
    <mat-label>Username</mat-label>
    <input matInput type="text" id="username" [(ngModel)]="user.username" name="username" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Full Name</mat-label>
    <input matInput type="text" id="fullName" [(ngModel)]="user.fullName" name="fullName" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Email</mat-label>
    <input matInput type="email" id="email" [(ngModel)]="user.email" name="email" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Mobile</mat-label>
    <input matInput type="text" id="mobile" [(ngModel)]="user.mobile" name="mobile" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Password</mat-label>
    <input matInput type="password" id="password" [(ngModel)]="user.password" name="password" required>
  </mat-form-field>

  <mat-form-field appearance="fill">
    <mat-label>Address</mat-label>
    <textarea matInput id="address" [(ngModel)]="user.address" name="address" required></textarea>
  </mat-form-field>

  <button mat-raised-button color="primary" type="submit">Sign Up</button>
</form>
