import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-review-rating',
  templateUrl: './review-rating.component.html',
  styleUrls: ['./review-rating.component.scss']
})
export class ReviewRatingComponent {
  public review: string = '';
  public stars = [1,2,3,4,5];
  public selectedStarIndex: number | null = null;
  public hoverIndex: number | null = null;

  @Output() reviewSubmitted = new EventEmitter<{rating: number, review: string}>();

  constructor(public http: HttpClient){}

  public rate(index: number){
    this.selectedStarIndex = index;
  }

  public hover(index: number) {
    this.hoverIndex = index;
  }

  public leave(){
    this.hoverIndex = null;
  }

  public submitReview(){
    if(this.selectedStarIndex !== null && this.review.trim() !== ''){
      this.http.post('/api/save-review', {rating: this.selectedStarIndex + 1, review: this.review})
        .subscribe((response: any) => {
          this.reviewSubmitted.emit({rating: this.selectedStarIndex!, review: this.review});
        }, (error: any) => {
          console.error('Error saving review:', error);
        });
    }
  }
}
