import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { AuthService } from '../auth/auth.service';

@Component({
  selector: 'app-edit-profile-dialog',
  templateUrl: './edit-profile-dialog.component.html',
  styleUrls: ['./edit-profile-dialog.component.scss']
})
export class EditProfileDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<EditProfileDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any,
    public authService: AuthService
  ){}

  public onNoClick(){
    this.dialogRef.close();
  }

  public onSaveClick(){
    this.authService.updateProfile(this.data.user.id, this.data.user).subscribe(
      updatedUser => {
        this.dialogRef.close(this.data.user);
      },
      error => {
        console.error('Error updating profile:', error);
      }
    )
  }
}
<h2 mat-dialog-title>Edit Profile</h2>
<div mat-dialog-content>
  <mat-form-field appearance="fill">
    <mat-label>Username</mat-label>
    <input matInput [(ngModel)]="data.user.username" name="username" required>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Full Name</mat-label>
    <input matInput [(ngModel)]="data.user.full_name" name="full_name" required>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Email</mat-label>
    <input matInput [(ngModel)]="data.user.email" name="email" required>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Mobile</mat-label>
    <input matInput [(ngModel)]="data.user.mobile_number" name="mobile_number" required>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Address</mat-label>
    <input matInput [(ngModel)]="data.user.address" name="address" required>
  </mat-form-field>
</div>
<div mat-dialog-actions>
  <button mat-button (click)="onNoClick()">Cancel</button>
  <button mat-button color="primary" (click)="onSaveClick()">Save</button>
</div>
