import { Component, Inject } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { NotificationService } from '../services/notification.service';
import { MenuService } from '../services/menu.service';
import { Menu } from '../models/menu.model';

@Component({
  selector: 'app-menu-dashboard',
  templateUrl: './menu-dashboard.component.html',
  styleUrls: ['./menu-dashboard.component.scss']
})
export class MenuDashboardComponent {
  public menu: Menu = { restaurantId: 0, name: '', description: '', price: 0 };

  constructor(
    public dialogRef: MatDialogRef<MenuDashboardComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any,
    public notificationService: NotificationService,
    public menuService: MenuService
  ) {
    this.menu.restaurantId = data.restaurant.id;
  }

  public saveMenu() {
    if (!this.menu.name || !this.menu.price) {
      this.notificationService.showError('Name and Price are required');
      return;
    }

    if (this.menu.id) {
      this.menuService.updateMenu(this.menu.id, this.menu).subscribe(
        () => {
          this.notificationService.showSuccess('Menu updated successfully');
          this.dialogRef.close(true);
        },
        error => {
          this.notificationService.showError('Error updating menu: ' + error.message);
          console.error('Error updating menu:', error);
        }
      );
    } else {
      this.menuService.saveMenu(this.menu.restaurantId, this.menu).subscribe(
        () => {
          this.notificationService.showSuccess('Menu saved successfully');
          this.dialogRef.close(true);
        },
        error => {
          this.notificationService.showError('Error saving menu: ' + error.message);
          console.error('Error saving menu:', error);
        }
      );
    }
  }
}
<h2>Manage Menu for {{data.restaurant.name}}</h2>

<form (ngSubmit)="saveMenu()" #menuForm="ngForm">
  <mat-form-field>
    <mat-label>Name</mat-label>
    <input matInput [(ngModel)]="menu.name" name="name" required>
    <mat-error *ngIf="menuForm.controls['name']?.invalid && menuForm.controls['name']?.touched">
      Name is required
    </mat-error>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Description</mat-label>
    <textarea matInput [(ngModel)]="menu.description" name="description"></textarea>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Price</mat-label>
    <input matInput type="number" [(ngModel)]="menu.price" name="price" required>
    <mat-error *ngIf="menuForm.controls['price']?.invalid && menuForm.controls['price']?.touched">
      Price is required
    </mat-error>
  </mat-form-field>
  <button mat-raised-button color="primary" type="submit" [disabled]="menuForm.invalid">Save Menu Item</button>
</form>
