import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { RestaurantService } from '../services/restaurant.service';
import { NotificationService } from '../services/notification.service';
import { MenuService } from '../services/menu.service';
import { Menu } from '../models/menu.model';
import { MenuDashboardComponent } from '../menu-dashboard/menu-dashboard.component';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent {
  public restaurant = { name: '', address: '', image: '', mobile: '' };
  public restaurants: any[] = [];
  public selectedRestaurant: any;
  public menus: Menu[] = [];

  constructor(
    public restaurantService: RestaurantService,
    public notificationService: NotificationService,
    public menuService: MenuService,
    public dialog: MatDialog
  ){}

  ngOnInit() {
    this.fetchRestaurants();
  }

  public saveRestaurant() {
    if (this.selectedRestaurant) {
      this.restaurantService.updateRestaurant(this.selectedRestaurant.id, this.restaurant).subscribe(
        (response) => {
          this.notificationService.showSuccess('Restaurant updated successfully');
          this.fetchRestaurants();
          this.resetForm();
        },
        error => {
          this.notificationService.showError('Error updating restaurant: ' + error.message);
          console.error('Error updating restaurant:', error);
        }
      );
    } else {
      this.restaurantService.saveRestaurant(this.restaurant).subscribe(
        (response) => {
          this.notificationService.showSuccess('Restaurant saved successfully');
          this.fetchRestaurants();
          this.resetForm();
          console.log('Restaurant saved successfully');
        },
        error => {
          this.notificationService.showError('Error saving restaurant: ' + error.message);
          console.error('Error saving restaurant:', error);
        }
      );
    }
  }

  public fetchRestaurants() {
    this.restaurantService.getRestaurants().subscribe(
      (data: any[]) => {
        this.restaurants = data;
      },
      (error) => {
        this.notificationService.showError('Error fetching restaurants: ' + error.message);
        console.error('Error fetching restaurants:', error);
      }
    );
  }

  public editRestaurant(restaurant: any) {
    this.selectedRestaurant = { ...restaurant };
    this.restaurant = { ...restaurant };
    this.fetchMenus(restaurant.id);
  }

  public deleteRestaurant(restaurant: any) {
    this.restaurantService.deleteRestaurant(restaurant.id).subscribe(
      () => {
        this.notificationService.showSuccess('Restaurant deleted successfully');
        this.fetchRestaurants();
      },
      error => {
        this.notificationService.showError('Error deleting restaurant: ' + error.message);
      }
    );
  }

  public viewMenus(restaurant: any){
    this.selectedRestaurant = restaurant;
    this.fetchMenus(restaurant.id);
  }

  public fetchMenus(restaurantId: number) {
    this.menuService.getMenus(restaurantId).subscribe(
      (data: Menu[]) => {
        this.menus = data;
      },
      (error) => {
        this.notificationService.showError('Error fetching menus: ' + error.message);
        console.error('Error fetching menus:', error);
      }
    );
  }

  public openAddMenuDialog(restaurant: any) {
    const dialogRef = this.dialog.open(MenuDashboardComponent, {
      width: '400px',
      data: { restaurant }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.fetchMenus(restaurant.id);
      }
    });
  }

  public openEditMenuDialog(menu: Menu) {
    const dialogRef = this.dialog.open(MenuDashboardComponent, {
      width: '400px',
      data: { restaurant: this.selectedRestaurant, menu }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.fetchMenus(this.selectedRestaurant.id);
      }
    });
  }

  public deleteMenu(menu: Menu) {
    if (menu.id) {
      this.menuService.deleteMenu(menu.id).subscribe(
        () => {
          this.notificationService.showSuccess('Menu deleted successfully');
          this.fetchMenus(menu.restaurantId);
        },
        error => {
          this.notificationService.showError('Error deleting menu: ' + error.message);
          console.error('Error deleting menu:', error);
        }
      );
    }
  }

  public resetForm() {
    this.selectedRestaurant = null;
    this.restaurant = { name: '', address: '', image: '', mobile: '' };
    this.menus = [];
  }
}
<h1>Add New Restaurant</h1>
<form class="dashboard-form">
  <mat-form-field>
    <mat-label>Restaurant Name</mat-label>
    <input matInput [(ngModel)]="restaurant.name" name="name" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Address</mat-label>
    <input matInput [(ngModel)]="restaurant.address" name="address" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Image URL</mat-label>
    <input matInput [(ngModel)]="restaurant.image" name="image" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Mobile Number</mat-label>
    <input matInput [(ngModel)]="restaurant.mobile" name="mobile" required>
  </mat-form-field>
  <button mat-raised-button color="primary" (click)="saveRestaurant()">
    {{selectedRestaurant ? 'Update': 'Save'}}
  </button>
</form>

<!-- show restaurant list -->
<div class="restaurant-container" style="display: flex;">
  <mat-card *ngFor="let restaurant of restaurants" class="restaurant-card">
    <mat-card-header>
      <img mat-card-avatar [src]="restaurant.image" alt="Restaurant Image">
      <mat-card-title>{{ restaurant.name }}</mat-card-title>
      <mat-card-subtitle>{{ restaurant.address }}</mat-card-subtitle>
      <mat-card-subtitle>{{ restaurant.mobile }}</mat-card-subtitle>
    </mat-card-header>
    <mat-card-actions>
      <button mat-button color="primary" (click)="editRestaurant(restaurant)">Edit</button>
      <button mat-button color="warn" (click)="deleteRestaurant(restaurant)">Delete</button>
      <button mat-button color="accent" (click)="viewMenus(restaurant.id)">View Menus</button>
      <button mat-button color="accent" (click)="openAddMenuDialog(restaurant)">Add Menu</button>
    </mat-card-actions>
  </mat-card>
</div>

<!-- Menu items section -->
<div *ngIf="selectedRestaurant">
  <h3>Menu Items for {{selectedRestaurant.name}}</h3>
  <mat-list>
    <mat-list-item *ngFor="let menuItem of menus">
      <h4 matLine>{{menuItem.name}}</h4>
      <p matLine>{{menuItem.description}}</p>
      <p matLine>${{menuItem.price}}</p>
      <button mat-button color="primary" (click)="openEditMenuDialog(menuItem)">Edit</button>
      <button mat-button color="warn" (click)="deleteMenu(menuItem)">Delete</button>
    </mat-list-item>
  </mat-list>
</div>
