import { Component } from '@angular/core';
import { RestaurantService } from '../services/restaurant.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent {
  public restaurant = { name: '', address: '', image: '', mobile: ''};

  constructor(public restaurantService: RestaurantService){}

  public saveRestaurant(){
    this.restaurantService.saveRestaurant(this.restaurant).subscribe(
      (response) => {
        console.log('Restaurant saved successfully');
      },
      error => {
        console.error('Error saving restaurant:', error);
      }
    );
  }
}
<h1>Add New Restaurant</h1>
<form class="dashboard-form">
  <mat-form-field>
    <mat-label>Restaurant Name</mat-label>
    <input matInput [(ngModel)]="restaurant.name" name="name" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Address</mat-label>
    <input matInput [(ngModel)]="restaurant.address" name="address" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Image URL</mat-label>
    <input matInput [(ngModel)]="restaurant.image" name="image" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Mobile Number</mat-label>
    <input matInput [(ngModel)]="restaurant.mobile" name="mobile" required>
  </mat-form-field>
  <button mat-raised-button color="primary" (click)="saveRestaurant()">Save</button>
</form>

<!-- show restaurant list -->
<div class="restaurant-container" style="display: flex;">
  <mat-card *ngFor="let restaurant of restaurants" class="restaurant-card">
    <mat-card-header>
      <img mat-card-avatar [src]="restaurant.image" alt="Restaurant Image">
      <mat-card-title>{{ restaurant.name }}</mat-card-title>
      <mat-card-subtitle>{{ restaurant.address }}</mat-card-subtitle>
      <mat-card-subtitle>{{ restaurant.mobile }}</mat-card-subtitle>
    </mat-card-header>
  </mat-card>
</div>
