import { Component } from '@angular/core';
import { OrderService } from '../services/order.service';
import { OrderHistory } from '../models/order-history.model';
import { AuthService } from '../auth/auth.service';
import { MatDialog } from '@angular/material/dialog';
import { EditProfileDialogComponent } from '../edit-profile-dialog/edit-profile-dialog.component';
import { NotificationService } from '../services/notification.service';
import { Address } from '../models/user.model';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent {
  public orderHistory: OrderHistory[] = [];
  public user: any;
  public addresses: Address[] = [];

  constructor(
    public orderService: OrderService, 
    public authService: AuthService,
    public dialog: MatDialog,
    public notificationService: NotificationService
  ){}

  ngOnInit(){
    this.fetchOrderHistory();
    this.fetchUserDetails();
  }

  // fetch user details
  // public fetchUserDetails(){
  //   this.authService.currentUser$.subscribe(
  //     (user) => {
  //       if (user) {
  //         this.user = user;
          
  //       }
  //     }
  //   );
  // }
  public fetchUserDetails(){
    const currentUser = this.authService.getCurrentUser();
    if (currentUser){
      this.user = currentUser;
      // this.fetchUserAddresses(currentUser.id);
      this.authService.getUserAddresses(currentUser.id).subscribe(
        (addresses: Address[]) => {
          if (addresses.length > 0){
            this.user.address = addresses[0];
          } else {
            this.user.address = {
              address_line1: '',
              address_line2: '',
              city: '',
              state: '',
              postal_code: '',
              country: ''
            };
          }
        },
        error => {
          console.error('Error fetching addresses:', error);
        }
      );
    }
  }

  // for fetch user address
  public fetchUserAddresses(userId: number) {
    this.authService.getUserAddresses(userId).subscribe(
      (addresses: Address[]) => {
        this.addresses = addresses;
      },
      error => {
        console.error('Error fetching addresses:', error);
      }
    );
  }

  // fetch order details
  public fetchOrderHistory(){
    this.orderService.getOrderHistory().subscribe(
      (data: OrderHistory[]) => {
        this.orderHistory = data;
      },
      error => {
        console.error('Error fetching order history:', error);
      }
    );
  }

  // open profile editing dialog
  public openEditProfileDialog(){
    const dialogRef = this.dialog.open(EditProfileDialogComponent, {
      width: '500px',
      data: {user: this.user}
    });
    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.authService.updateProfile(this.user.id, result).subscribe(
          updatedUser => {
            this.user = updatedUser;
            this.notificationService.showSuccess('Profile updated successfully');
          },
          error => {
            console.error('Error updating profile:', error);
            this.notificationService.showError('Failed to update profile');
          }
        );
        this.authService.updateAddress(this.user.id, result.address).subscribe(
          updateAddress => {
            this.user.address = updateAddress;
            this.notificationService.showSuccess('Address updated successfully');
          },
          error => {
            console.error('Error updating address:', error);
            this.notificationService.showError('Failed to update address');
          }
        )
      }
    })
  }
  
  public submitReviewRating(reviewRating: {rating: number, review: string}, order: OrderHistory){
    this.orderService.submitReview(order.id, reviewRating).subscribe(
      updatedOrder => {
        const orderIndex = this.orderHistory.findIndex(o => o.id === updatedOrder.id);
        if (orderIndex !== -1){
          this.orderHistory[orderIndex] = updatedOrder;
          this.orderHistory[orderIndex].rating = parseFloat(updatedOrder.rating.toFixed(1));
        }
        this.notificationService.showSuccess('Review submitted successfully');
      },
      error => {
        console.error('Error submitting review:', error);
        this.notificationService.showError('Failed to submit review');
      }
    );
  }
}
<div class="profile">
  <h2>User Profile</h2>
  <!-- profile details -->
  <div *ngIf="user" class="profile-details">
    <mat-card>
      <mat-card-content>
        <div><strong>Username:</strong> {{ user.username }}</div>
        <div><strong>Full Name:</strong> {{ user.full_name }}</div>
        <div><strong>Email:</strong> {{ user.email }}</div>
        <div><strong>Mobile:</strong> {{ user.mobile_number }}</div>
        <div *ngFor="let address of addresses">
          <div><strong>Address Line 1:</strong> {{ address.address_line1 }}</div>
          <div><strong>Address Line 2:</strong> {{ address.address_line2 }}</div>
          <div><strong>City:</strong> {{ address.city }}</div>
          <div><strong>State:</strong> {{ address.state }}</div>
          <div><strong>Postal Code:</strong> {{ address.postal_code }}</div>
          <div><strong>Country:</strong> {{ address.country }}</div>
        </div>
      </mat-card-content>
      <mat-card-actions>
        <button mat-button color="primary" (click)="openEditProfileDialog()">Edit Profile</button>
      </mat-card-actions>
    </mat-card>
  </div>
  <!-- order history -->
  <div class="order-history">
    <h2>Order History</h2>
    <mat-card>
      <mat-card-content>
        <ul style="list-style-type: none; display: flex; flex-wrap: wrap;">
          <li *ngFor="let order of orderHistory" style="margin: 10px; padding: 10px; border: 1px solid lightgray; border-radius: 5px;">
            <div><strong>Order Date:</strong> {{ order.order_date | date: 'short' }}</div>
            <div><strong>Restaurant:</strong> {{ order.restaurant_name }}</div>
            <div><strong>Menu Item:</strong> {{ order.menu_item_name }}</div>
            <div><strong>Quantity:</strong> {{ order.quantity }}</div>
            <div><strong>Total Price:</strong> {{ order.total_price | currency: 'INR' }}</div>
            <div *ngIf="!order.rating">
              <app-review-rating (reviewSubmitted)="submitReviewRating($event, order)"></app-review-rating>
            </div>
            <div *ngIf="order.rating">
              <div><strong>Your Rating:</strong>{{order.rating}}</div>
              <div><strong>Your Review:</strong>{{order.review}}</div>
            </div>
          </li>
        </ul>
      </mat-card-content>
    </mat-card>
  </div>
</div>
