import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { AuthService } from '../auth/auth.service';
import { Observable } from 'rxjs';
import { OrderHistory } from '../models/order-history.model';

@Injectable({
  providedIn: 'root'
})
export class OrderService {

  constructor(public http: HttpClient, public authService: AuthService) { }

  public getOrderHistory(): Observable<any> {
    const user = this.authService.getCurrentUser();
    return this.http.get<OrderHistory[]>(`/api/users/${user.id}/orders`);
  }

  public saveReview(orderId: number, rating: number, review: string, restaurantId: number): Observable<any> {
    return this.http.post('/api/save-review', {rating, review, orderId, restaurantId});
  }
}
