import { Component } from '@angular/core';
import { Menu } from '../models/menu.model';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent {
  public cartItems: { menuItem: Menu, quantity: number }[] = [];

  constructor(public cartService: CartService){}

  ngOnInit(){
    this.cartItems = this.cartService.getCartItems();
  }

  public clearCart(){
    this.cartService.clearCart();
    this.cartItems = [];
  }

  public incrementQuantity(item: {menuItem: Menu, quantity: number}){
    this.cartService.addToCart(item.menuItem, 1);
    item.quantity;
  }

  public decrementQuantity(item: {menuItem: Menu, quantity: number}) {
    if (item.quantity > 0) {
      this.cartService.addToCart(item.menuItem, -1);
      item.quantity;
    }
  }
}
<div class="container mt-4">
  <h2>Your Cart</h2>
  <mat-list>
    <mat-list-item *ngFor="let item of cartItems" class="d-flex justify-content-between align-items-center">
      <div>
        <h4 class="mb-1">{{item.menuItem.name}}</h4>
        <p class="mb-1">{{item.menuItem.description}}</p>
        <p class="mb-1">₹{{item.menuItem.price | number: '1.2-2'}}</p>
        <p class="mb-1">Quantity: {{item.quantity}}</p>
        <div class="btn-group">
          <button mat-button (click)="decrementQuantity(item)">-</button>
          <button mat-button disabled>{{item.quantity}}</button>
          <button mat-button (click)="incrementQuantity(item)">+</button>
        </div>
      </div>
    </mat-list-item>
  </mat-list>
  <button mat-raised-button color="warn" class="mt-3" (click)="clearCart()">Clear Cart</button>
</div>
