import { Component } from '@angular/core';
import { RestaurantService } from '../services/restaurant.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  public restaurants: any[] = [
    { name: "Restaurant 1", address: 'Address 1', image: 'https://dummyimage.com/600x400/000/fff&text=restaurant1' },
    { name: "Restaurant 2", address: 'Address 2', image: 'https://dummyimage.com/600x400/000/fff&text=restaurant2' },
    { name: "Restaurant 3", address: 'Address 3', image: 'https://dummyimage.com/600x400/000/fff&text=restaurant3' },
    { name: "Restaurant 4", address: 'Address 4', image: 'https://dummyimage.com/600x400/000/fff&text=restaurant4' },
  ]
}
<div class="restaurant-container">
  <mat-card *ngFor="let restaurant of restaurants" class="restaurant-card">
    <mat-card-header>
      <img mat-card-avatar [src]="restaurant.image" alt="Restaurant Image">
      <mat-card-title>{{ restaurant.name }}</mat-card-title>
      <mat-card-subtitle>{{ restaurant.address }}</mat-card-subtitle>
    </mat-card-header>
  </mat-card>
</div>
